from .._sample.create_binary_classification_data import CreateBinaryClassificationData


def create_binary_classification_data(input_data, input_label, label_class_field, output_path, output_name,
                                      training_data_format,
                                      tile_format='jpg', tile_size_x=1024, tile_size_y=1024, tile_offset_x=512,
                                      tile_offset_y=512,
                                      tile_start_index=0,
                                      save_nolabel_tiles=False,**kwargs):
    create_td = CreateBinaryClassificationData(input_data, input_label, label_class_field, output_path, output_name,
                                               training_data_format,
                                               tile_format, tile_size_x, tile_size_y, tile_offset_x,
                                               tile_offset_y,
                                               tile_start_index,
                                               save_nolabel_tiles,**kwargs)
    create_td.create_binary_classification()
