import torch
import torch.nn as nn
import torch.nn.functional as F

class ConvBNReLU(nn.Module):
    def __init__(self, in_channels, out_channels, kernel_size=3):
        super().__init__()
        self.block = nn.Sequential(
            nn.Conv2d(in_channels, out_channels, (kernel_size, kernel_size), stride=1, padding=kernel_size//2, bias=False),
            nn.BatchNorm2d(out_channels),
            nn.ReLU())
    def forward(self, x):
        x = self.block(x)
        return x


class FPNBlock(nn.Module):
    def __init__(self, pyramid_channels, skip_channels):
        super().__init__()
        self.skip_conv = nn.Sequential(
            nn.Conv2d(skip_channels, pyramid_channels, kernel_size=1),
            nn.BatchNorm2d(pyramid_channels),
            nn.ReLU())
        self.AlignedModule = AlignedModule(pyramid_channels, pyramid_channels//2)

    def forward(self, x, skip=None):
        # x = F.interpolate(x, scale_factor=2, mode="nearest")
        skip = self.skip_conv(skip)
        x = self.AlignedModule([skip, x])
        x = x + skip
        return x

class SegmentationBlock(nn.Module):
    def __init__(self, in_channels, out_channels, n_upsamples=0):
        super().__init__()

        blocks = [ConvBNReLU(in_channels, out_channels, upsample=bool(n_upsamples))]

        if n_upsamples > 1:
            for _ in range(1, n_upsamples):
                blocks.append(ConvBNReLU(out_channels, out_channels, upsample=True))

        self.block = nn.Sequential(*blocks)

    def forward(self, x):
        return self.block(x)


class MergeBlock(nn.Module):
    def __init__(self, policy, in_channels, out_channels):
        super().__init__()
        if policy not in ["add", "cat"]:
            raise ValueError(
                "`merge_policy` must be one of: ['add', 'cat'], got {}".format(
                    policy
                )
            )
        if policy == 'cat':
            in_channels = in_channels * 4
        self.policy = policy
        self.last_conv = ConvBNReLU(in_channels, out_channels)

    def forward(self, x):
        if self.policy == 'add':
            return self.last_conv(sum(x))
        elif self.policy == 'cat':
            return self.last_conv(torch.cat(x, dim=1))
        else:
            raise ValueError(
                "`merge_policy` must be one of: ['add', 'cat'], got {}".format(self.policy)
            )


class DSAMNetDecoder(nn.Module):
    def __init__(
            self,
            encoder_channels,
            encoder_depth=5,
            pyramid_channels=96,
            segmentation_channels=64,
            dropout=0.5,
            merge_policy="cat",
    ):
        super().__init__()
        if encoder_channels[-4] > 64:
            segmentation_channels = 256
            pyramid_channels = 256
        self.out_channels = segmentation_channels  # if merge_policy == "add" else segmentation_channels * 4
        if encoder_depth < 3:
            raise ValueError("Encoder depth for FPN decoder cannot be less than 3, got {}.".format(encoder_depth))
        self.head = UperNetHead(encoder_channels, encoder_depth, pyramid_channels, segmentation_channels, dropout)

    def forward(self, *features):
        features1, features2 = features[0], features[1]
        c11, c12, c13, c14 = features1
        c21, c22, c23, c24 = features2
        out1 = self.head([c11, c12, c13, c14])
        out2 = self.head([c21, c22, c23, c24])

        return [out1, out2]


class UperNetHead(nn.Module):
    def __init__(
            self,
            encoder_channels,
            encoder_depth=5,
            pyramid_channels=96,
            segmentation_channels=128,
            dropout=0.5):
        super(UperNetHead, self).__init__()

        self.out_channels = segmentation_channels  # if merge_policy == "add" else segmentation_channels * 4
        if encoder_depth < 3:
            raise ValueError("Encoder depth for FPN decoder cannot be less than 3, got {}.".format(encoder_depth))

        encoder_channels = encoder_channels[::-1]
        encoder_channels = encoder_channels[:encoder_depth + 1]

        self.fpn_in = []
        for inplane in encoder_channels[0:4][::-1]:
            self.fpn_in.append(
                nn.Sequential(
                    ConvBNReLU(inplane, pyramid_channels, kernel_size=1)
                ))
        self.fpn_in = nn.ModuleList(self.fpn_in)
        self.merge = nn.Sequential(
            ConvBNReLU(pyramid_channels * 4, segmentation_channels * 4),
            nn.Dropout2d(p=dropout),
            ConvBNReLU(segmentation_channels * 4, segmentation_channels, 1))

    def forward(self, features):
        conv_out = features[-4:]
        feature_pyramid = []
        for i in reversed(range(len(conv_out))):
            feature_pyramid.append(self.fpn_in[i](conv_out[i]))
        feature_pyramid.reverse()  # [P2 - P5]
        output_size = feature_pyramid[0].size()[2:]
        fusion_list = [feature_pyramid[0]]

        for i in range(1, len(feature_pyramid)):
            fusion_list.append(F.interpolate(feature_pyramid[i], output_size, mode='bilinear', align_corners=True))
        x = self.merge(torch.cat(fusion_list, 1))

        return x
class PyramidPooling(nn.Module):
    """
    Reference:
        Zhao, Hengshuang, et al. *"Pyramid scene parsing network."*
    """

    def __init__(self, in_channels, out_channels, norm_layer=nn.BatchNorm2d):
        super(PyramidPooling, self).__init__()
        self.pool1 = nn.AdaptiveAvgPool2d(1)
        self.pool2 = nn.AdaptiveAvgPool2d(2)
        self.pool3 = nn.AdaptiveAvgPool2d(3)
        self.pool4 = nn.AdaptiveAvgPool2d(6)

        #out_channels = int(in_channels / 4)
        self.conv1 = nn.Sequential(nn.Conv2d(in_channels, out_channels, 1, bias=False),
                                   norm_layer(out_channels),
                                   nn.ReLU(True))
        self.conv2 = nn.Sequential(nn.Conv2d(in_channels, out_channels, 1, bias=False),
                                   norm_layer(out_channels),
                                   nn.ReLU(True))
        self.conv3 = nn.Sequential(nn.Conv2d(in_channels, out_channels, 1, bias=False),
                                   norm_layer(out_channels),
                                   nn.ReLU(True))
        self.conv4 = nn.Sequential(nn.Conv2d(in_channels, out_channels, 1, bias=False),
                                   norm_layer(out_channels),
                                   nn.ReLU(True))
        self.bottleneck = nn.Sequential(
            nn.Conv2d(in_channels + 4 * out_channels, out_channels, kernel_size=1, padding=0, dilation=1, bias=False),
            norm_layer(out_channels),
            nn.ReLU(),
            nn.Dropout2d(0.1)
        )

    def forward(self, x):
        _, _, h, w = x.size()
        feat1 = F.interpolate(self.conv1(self.pool1(x)), (h, w), mode='bilinear', align_corners=True)
        feat2 = F.interpolate(self.conv2(self.pool2(x)), (h, w), mode='bilinear', align_corners=True)
        feat3 = F.interpolate(self.conv3(self.pool3(x)), (h, w), mode='bilinear', align_corners=True)
        feat4 = F.interpolate(self.conv4(self.pool4(x)), (h, w), mode='bilinear', align_corners=True)
        return self.bottleneck(torch.cat((x, feat1, feat2, feat3, feat4), 1))

class AlignedModule(nn.Module):
    """
    Reference:
        Xiangtai Li, et al. *"Semantic Flow for Fast and Accurate Scene Parsing."*
    """

    def __init__(self, inplane, outplane, kernel_size=3):
        super(AlignedModule, self).__init__()
        self.down_h = nn.Conv2d(inplane, outplane, 1, bias=False)
        self.down_l = nn.Conv2d(inplane, outplane, 1, bias=False)
        self.flow_make = nn.Conv2d(outplane*2, 2, kernel_size=kernel_size, padding=1, bias=False)

    def forward(self, x):
        low_feature, h_feature = x
        h_feature_orign = h_feature
        h, w = low_feature.size()[2:]
        size = (h, w)
        low_feature = self.down_l(low_feature)
        h_feature = self.down_h(h_feature)
        h_feature = F.interpolate(h_feature, size=size, mode="bilinear", align_corners=True)
        flow = self.flow_make(torch.cat([h_feature, low_feature], 1))
        h_feature = self.flow_warp(h_feature_orign, flow, size=size)

        return h_feature

    def flow_warp(self, input, flow, size):
        out_h, out_w = size
        n, c, h, w = input.size()
        # n, c, h, w
        # n, 2, h, w

        norm = torch.tensor([[[[out_w, out_h]]]]).type_as(input).to(input.device)
        h = torch.linspace(-1.0, 1.0, out_h).view(-1, 1).repeat(1, out_w)
        w = torch.linspace(-1.0, 1.0, out_w).repeat(out_h, 1)
        grid = torch.cat((w.unsqueeze(2), h.unsqueeze(2)), 2)
        grid = grid.repeat(n, 1, 1, 1).type_as(input).to(input.device)
        grid = grid + flow.permute(0, 2, 3, 1) / norm

        output = F.grid_sample(input, grid, align_corners=False)
        return output