import re
import torch.nn as nn
import torch
import copy
from pretrainedmodels.models.xception import pretrained_settings
from pretrainedmodels.models.xception import Xception

from iobjectspy.ml.vision._models.semantic_seg._torch_models.encoders.xception_atrous import XceptionAtrousEncoder
from ._base import EncoderMixin


class XceptionEncoder(Xception, EncoderMixin):

    def __init__(self, out_channels, *args, depth=5, **kwargs):
        super().__init__(*args, **kwargs)

        self._out_channels = out_channels
        self._depth = depth
        self._in_channels = 3

        # modify padding to maintain output shape
        self.conv1.padding = (1, 1)
        self.conv2.padding = (1, 1)

        del self.fc

    def make_dilated(self, stage_list, dilation_list):
        raise ValueError("Xception encoder does not support dilated mode "
                         "due to pooling operation for downsampling!")

    def get_stages(self):
        return [
            nn.Identity(),
            nn.Sequential(self.conv1, self.bn1, self.relu, self.conv2, self.bn2, self.relu),
            self.block1,
            self.block2,
            self.block3,
            nn.Sequential(self.block4, self.block5, self.block6, self.block7,
                          self.block8, self.block9, self.block10, self.block11),
            nn.Sequential(self.block12, self.conv3, self.bn3, self.relu, self.conv4, self.bn4),
        ]

    def forward(self, x):
        stages = self.get_stages()

        features = []
        for i in range(self._depth + 2):
            x = stages[i](x)
            features.append(x)

        return features

    def load_state_dict(self, model_dict, in_channels=3):
        state_dict = copy.deepcopy(model_dict)
        # remove linear
        state_dict.pop('fc.bias')
        state_dict.pop('fc.weight')
        if in_channels != 3:
            pretrained_dict = {}
            for i, (k, weight) in enumerate(state_dict.items()):
                if i == 0:
                    if in_channels == 1:
                        weight = weight.sum(1, keepdim=True)
                    elif in_channels == 2:
                        weight = weight[:, :2] * (3.0 / 2.0)
                    else:
                        weight_base = [weight, ] * (in_channels // 3) + [weight[:, :in_channels % 3], ]
                        weight = torch.cat(weight_base, dim=1)
                    pretrained_dict[k] = weight
                else:
                    pretrained_dict[k] = weight
            # overwrite entries in the existing state dict
            state_dict.update(pretrained_dict)
        return super().load_state_dict(state_dict)


xception_encoders = {
    'xception': {
        'encoder': XceptionEncoder,
        'pretrained_settings': pretrained_settings['xception'],
        'params': {
            'out_channels': (3, 64, 128, 256, 728, 2048),
        }
    },
    'xception_atrous': {
        'encoder': XceptionAtrousEncoder,
        'pretrained_settings': pretrained_settings['xception'],
        'params': {
            'out_channels': (3, 64, 128, 256, 728, 2048),
        }
    },
}
