from typing import Optional, Union
from .decoder import SegFormerDecoder
from ..base import SegmentationModel, ClassificationHead, SegmentationHead, SegmentationHead_SiameCD,\
    SegmentationHead_SiameConcatCD, SegmentationHead_SiameAddCD
from ..encoders import get_encoder


class SegFormer(SegmentationModel):
    """SegFormer_ is a vision transformer network for image semantic segmentation
    Args:
        encoder_name: name of classification model (without last dense layers) used as feature
                extractor to build segmentation model.
        encoder_depth: number of stages used in decoder, larger depth - more features are generated.
            e.g. for depth=3 encoder will generate list of features with following spatial shapes
            [(H,W), (H/2, W/2), (H/4, W/4), (H/8, W/8)], so in general the deepest feature will have
            spatial resolution (H/(2^depth), W/(2^depth)]
        encoder_weights: one of ``None`` (random initialization), ``imagenet`` (pre-training on ImageNet).
        decoder_pyramid_channels: a number of convolution filters in decoder of segformer_.
        decoder_segmentation_channels: a number of convolution filters in segmentation head of segformer_.
        decoder_dropout: spatial dropout rate in range (0, 1).
        in_channels: number of input channels for model, default is 3.
        classes: a number of classes for output (output shape - ``(batch, classes, h, w)``).
        activation (str, callable): activation function used in ``.predict(x)`` method for inference.
            One of [``sigmoid``, ``softmax2d``, callable, None]
        upsampling: optional, final upsampling factor
            (default is 4 to preserve input -> output spatial shape identity)
        aux_params: if specified model will have additional classification auxiliary output
            build on top of encoder, supported params:
                - classes (int): number of classes
                - pooling (str): one of 'max', 'avg'. Default is 'avg'.
                - dropout (float): dropout factor in [0, 1)
                - activation (str): activation function to apply "sigmoid"/"softmax" (could be None to return logits)

    Returns:
        ``torch.nn.Module``: **SFNet**

    SegFormer:
        SegFormer: Simple and Efficient Design for Semantic Segmentation with Transformers

    """

    def __init__(
        self,
        encoder_name: str = "mit-b3",
        encoder_depth: int = 5,
        encoder_weights: Optional[str] = "imagenet",
        embedding_dim: int = 256,
        decoder_segmentation_channels: int = 256,
        decoder_dropout: float = 0.1,
        in_channels: int = 3,
        classes: int = 1,
        activation: Optional[str] = None,
        upsampling: int = 4,
        aux_params: Optional[dict] = None,
        siame_encoder: bool = True,
        head_merge_method: str = "subtract",
    ):
        super().__init__()
        self.siame_encoder = siame_encoder

        self.encoder = get_encoder(
            encoder_name,
            in_channels=in_channels,
            depth=encoder_depth,
            weights=encoder_weights,
        )

        self.decoder = SegFormerDecoder(
            encoder_channels=self.encoder.out_channels,
            encoder_depth=encoder_depth,
            embedding_dim=embedding_dim,
            segmentation_channels=decoder_segmentation_channels,
            dropout=decoder_dropout,
            siame_encoder=self.siame_encoder
        )
        if self.siame_encoder:
            if head_merge_method == "subtract":
                self.segmentation_head = SegmentationHead_SiameCD(
                in_channels=self.decoder.out_channels,
                out_channels=classes,
                activation=activation,
                kernel_size=1,
                upsampling=upsampling)
            elif head_merge_method == "cat":
                self.segmentation_head = SegmentationHead_SiameConcatCD(
                    in_channels=self.decoder.out_channels,
                    out_channels=classes,
                    activation=activation,
                    kernel_size=1,
                    upsampling=upsampling)
            else:
                self.segmentation_head = SegmentationHead_SiameAddCD(
                    in_channels=self.decoder.out_channels,
                    out_channels=classes,
                    activation=activation,
                    kernel_size=1,
                    upsampling=upsampling)
        else:
            self.segmentation_head = SegmentationHead(
                in_channels=self.decoder.out_channels,
                out_channels=classes,
                activation=activation,
                kernel_size=1,
                upsampling=upsampling,
            )

        if aux_params is not None:
            self.classification_head = ClassificationHead(
                in_channels=self.encoder.out_channels[-1], **aux_params
            )
        else:
            self.classification_head = None

        self.name = "segformer-{}".format(encoder_name)
        self.initialize()
